const path = require("path");

const bigInt = require("big-integer");
const Scalar = require("ffjavascript").Scalar;
const wasm_tester = require("circom_tester").wasm;

const { splitToArray } = require("./util.js");


describe("Mul mod bignumber", function () {
    this.timeout(100000);

    let circuit;
    before(async () => {
        circuit = await wasm_tester(path.join(__dirname, "circuits", "mul_mod.circom"));
    });

    it("64bits/64words. bignumber left shift", async () => {

        const a = bigInt("");
        const b = bigInt("");

        const modulus = bigInt("")
    
        var testCases = [{
            description: "Div shift 1",
            input: {
                // 1844674407370955161600
                a: splitToArray(a, 64, 32),
                b: splitToArray(b, 64, 32),
                modulus: splitToArray(modulus, 64, 32),
            },
            
            output: { 
                out: splitToArray(a.multiply(b).mod(modulus), 64, 32),
            },
        }];


        for (var i = 0; i < testCases.length; i++) {
            const witness = await circuit.calculateWitness(testCases[i].input, true);

            await circuit.assertOut(witness, testCases[i].output);
        }
    });
});
